import React, { useState } from 'react';
import axios from 'axios';
import '../css/style.css'; // Connecting to CSS

function Register() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    age: '',
    gender: '',
    destination: '',
    package: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/users/register', formData);
      alert(response.data.message);
    } catch (error) {
      console.error(error.response.data.message);
      alert('Registration failed');
    }
  };

  return (
    <div className="register-body">
      <div className="registration">
        <form onSubmit={handleSubmit}>
          <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
          <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
          <input type="text" name="phone" placeholder="Phone" value={formData.phone} onChange={handleChange} required />
          <input type="number" name="age" placeholder="Age" value={formData.age} onChange={handleChange} required />
          <input type="text" name="gender" placeholder="Gender" value={formData.gender} onChange={handleChange} required />
          <input type="text" name="destination" placeholder="Destination" value={formData.destination} onChange={handleChange} required />
          <input type="text" name="package" placeholder="Package" value={formData.package} onChange={handleChange} required />
          <button type="submit" className="submitbtn">Register</button>
        </form>
      </div>
    </div>
  );
}

export default Register;
