import React, { useState } from 'react';
import '../assets/css/style.css';
import { registerUser } from '../api/api'; // Connect to your backend API

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    age: '',
    gender: '',
    destination: [],
    package: '',
    terms: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await registerUser(formData); // Make the API call
      console.log('Registration successful:', response.data);
    } catch (error) {
      console.error('Error registering user', error);
    }
  };

  return (
    <section className="registration">
      <form className="register-form" onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
        <input type="tel" name="phone" placeholder="Phone" value={formData.phone} onChange={handleChange} required />
        <input type="number" name="age" placeholder="Age" value={formData.age} onChange={handleChange} required />

        <div>
          <label>Male</label>
          <input type="radio" name="gender" value="Male" onChange={handleChange} required />
          <label>Female</label>
          <input type="radio" name="gender" value="Female" onChange={handleChange} required />
        </div>

        <div>
          <label>Kashmir</label>
          <input type="checkbox" name="destination" value="Kashmir" onChange={handleChange} />
          <label>Istanbul</label>
          <input type="checkbox" name="destination" value="Istanbul" onChange={handleChange} />
          <label>Paris</label>
          <input type="checkbox" name="destination" value="Paris" onChange={handleChange} />
          {/* Add other destinations */}
        </div>

        <select name="package" value={formData.package} onChange={handleChange} required>
          <option value="">Select Package</option>
          <option value="Bronze">Bronze</option>
          <option value="Silver">Silver</option>
          <option value="Gold">Gold</option>
          <option value="Platinum">Platinum</option>
        </select>

        <div>
          <input type="checkbox" name="terms" checked={formData.terms} onChange={handleChange} required />
          <label>I accept the terms & conditions</label>
        </div>

        <button type="submit">Submit</button>
      </form>
    </section>
  );
};

export default Register;
